import React, { useState, useRef, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../styles/SymptomCapturePage.css';

const SymptomCapturePage = () => {
  const [isRecording, setIsRecording] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [interimTranscript, setInterimTranscript] = useState('');
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [selectedLanguage, setSelectedLanguage] = useState('en-IN'); // Default to English (India)
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitSuccess, setSubmitSuccess] = useState(false);
  
  const recognitionRef = useRef(null);
  const fileInputRef = useRef(null);

  // List of supported languages
  const languages = [
    { code: 'en-IN', name: 'English' },
    { code: 'hi-IN', name: 'Hindi' },
    { code: 'ta-IN', name: 'Tamil' },
    { code: 'bn-IN', name: 'Bengali' },
    { code: 'te-IN', name: 'Telugu' },
    { code: 'mr-IN', name: 'Marathi' },
    { code: 'gu-IN', name: 'Gujarati' },
    { code: 'kn-IN', name: 'Kannada' },
    { code: 'ml-IN', name: 'Malayalam' }
  ];

  // Initialize speech recognition
  useEffect(() => {
    // Check if browser supports SpeechRecognition
    if (!('webkitSpeechRecognition' in window) && !('SpeechRecognition' in window)) {
      alert('Your browser does not support speech recognition. Please try Chrome or Edge.');
      return;
    }

    // Initialize SpeechRecognition
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    recognitionRef.current = new SpeechRecognition();
    recognitionRef.current.continuous = true;
    recognitionRef.current.interimResults = true;
    recognitionRef.current.lang = selectedLanguage;

    recognitionRef.current.onresult = (event) => {
      let currentInterimTranscript = '';
      let finalTranscript = '';

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcriptText = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          finalTranscript += transcriptText + ' ';
        } else {
          currentInterimTranscript += transcriptText;
        }
      }

      // Update the final transcript if we have new final results
      if (finalTranscript) {
        setTranscript(prev => prev + finalTranscript);
      }
      
      // Update the interim transcript with current interim results
      setInterimTranscript(currentInterimTranscript);
    };

    recognitionRef.current.onerror = (event) => {
      console.error('Speech recognition error', event.error);
      setIsRecording(false);
    };

    recognitionRef.current.onend = () => {
      // Clear interim transcript when recognition ends
      setInterimTranscript('');
      
      if (isRecording) {
        // If still recording, restart recognition (for continuous recording)
        recognitionRef.current.start();
      }
    };

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, [selectedLanguage, isRecording]);

  // Update recognition language when language selection changes
  useEffect(() => {
    if (recognitionRef.current) {
      recognitionRef.current.lang = selectedLanguage;
    }
  }, [selectedLanguage]);

  const toggleRecording = () => {
    if (isRecording) {
      recognitionRef.current.stop();
      setIsRecording(false);
      setInterimTranscript(''); // Clear interim transcript when stopping
    } else {
      setIsRecording(true);
      recognitionRef.current.start();
    }
  };

  const handleFileUpload = (e) => {
    const files = Array.from(e.target.files);
    setUploadedFiles((prevFiles) => [...prevFiles, ...files]);
  };

  const removeFile = (index) => {
    setUploadedFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
  };

  const handleSubmit = async () => {
    if (!transcript.trim() && uploadedFiles.length === 0) {
      alert('Please provide symptom description or upload medical reports.');
      return;
    }

    setIsSubmitting(true);

    try {
      // Create form data to send files and text
      const formData = new FormData();
      formData.append('symptomDescription', transcript);
      formData.append('language', selectedLanguage);
      
      uploadedFiles.forEach((file, index) => {
        formData.append(`medicalReport_${index}`, file);
      });

      // Example API call - replace with your actual backend endpoint
      // const response = await fetch('https://your-backend-api.com/symptoms', {
      //   method: 'POST',
      //   body: formData,
      // });
      
      // Simulate API call for now
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // if (!response.ok) throw new Error('Failed to submit data');
      // const data = await response.json();
      
      setSubmitSuccess(true);
      // Redirect or show success message
    } catch (error) {
      console.error('Error submitting data:', error);
      alert('Failed to submit data. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const clearAll = () => {
    setTranscript('');
    setInterimTranscript('');
    setUploadedFiles([]);
    setSubmitSuccess(false);
  };

  return (
    <div className="symptom-capture-container">
      <div className="capture-header">
        <h1>Describe Your Symptoms</h1>
        <p>Speak or type your symptoms, and upload any relevant medical reports</p>
      </div>

      {submitSuccess ? (
        <div className="success-container">
          <div className="success-icon">
            <i className="fas fa-check-circle"></i>
          </div>
          <h2>Information Submitted Successfully!</h2>
          <p>We're analyzing your symptoms and finding the best doctor match for you.</p>
          <div className="success-actions">
            <Link to="/doctor-recommendations" className="primary-button">
              View Recommendations
            </Link>
            <button onClick={clearAll} className="secondary-button">
              Submit Another
            </button>
          </div>
        </div>
      ) : (
        <>
          <div className="language-selector">
            <label htmlFor="language-select">Select Language:</label>
            <select 
              id="language-select" 
              value={selectedLanguage} 
              onChange={(e) => setSelectedLanguage(e.target.value)}
              disabled={isRecording}
            >
              {languages.map((lang) => (
                <option key={lang.code} value={lang.code}>
                  {lang.name}
                </option>
              ))}
            </select>
          </div>

          <div className="voice-input-section">
            <div className="voice-controls">
              <button 
                className={`record-button ${isRecording ? 'recording' : ''}`} 
                onClick={toggleRecording}
                disabled={isSubmitting}
              >
                {isRecording ? (
                  <>
                    <i className="fas fa-stop-circle"></i> Stop Recording
                  </>
                ) : (
                  <>
                    <i className="fas fa-microphone"></i> Start Recording
                  </>
                )}
              </button>
              {isRecording && <div className="recording-indicator">Recording...</div>}
            </div>

            <div className="transcript-container">
              <h3>Your Symptom Description:</h3>
              <div className="transcript-wrapper">
                <textarea
                  value={transcript}
                  onChange={(e) => setTranscript(e.target.value)}
                  placeholder="Your spoken symptoms will appear here. You can also type or edit directly."
                  rows={8}
                  disabled={isSubmitting}
                ></textarea>
                {isRecording && interimTranscript && (
                  <div className="interim-transcript">
                    {transcript}{' '}
                    <span className="interim-text">{interimTranscript}</span>
                  </div>
                )}
              </div>
            </div>
          </div>

          <div className="file-upload-section">
            <h3>Upload Medical Reports (Optional)</h3>
            <p>Upload any relevant medical reports, test results, or previous diagnoses</p>
            
            <div className="upload-controls">
              <button 
                className="upload-button" 
                onClick={() => fileInputRef.current.click()}
                disabled={isSubmitting}
              >
                <i className="fas fa-file-upload"></i> Select Files
              </button>
              <input
                type="file"
                ref={fileInputRef}
                onChange={handleFileUpload}
                multiple
                accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
                style={{ display: 'none' }}
                disabled={isSubmitting}
              />
            </div>

            {uploadedFiles.length > 0 && (
              <div className="uploaded-files">
                <h4>Uploaded Files:</h4>
                <ul>
                  {uploadedFiles.map((file, index) => (
                    <li key={index}>
                      <i className="fas fa-file-medical"></i>
                      <span>{file.name}</span>
                      <button 
                        className="remove-file" 
                        onClick={() => removeFile(index)}
                        disabled={isSubmitting}
                      >
                        <i className="fas fa-times"></i>
                      </button>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          <div className="submission-section">
            <button 
              className="submit-button" 
              onClick={handleSubmit}
              disabled={isSubmitting || (!transcript.trim() && uploadedFiles.length === 0)}
            >
              {isSubmitting ? (
                <>
                  <i className="fas fa-spinner fa-spin"></i> Processing...
                </>
              ) : (
                <>
                  <i className="fas fa-paper-plane"></i> Submit Information
                </>
              )}
            </button>
            <button 
              className="clear-button" 
              onClick={clearAll}
              disabled={isSubmitting || (!transcript.trim() && uploadedFiles.length === 0)}
            >
              <i className="fas fa-trash"></i> Clear All
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default SymptomCapturePage;
