import React, { useState, useEffect } from 'react';
import '../styles/FindDoctorPage.css';

const FindDoctorPage = () => {
  const [doctors, setDoctors] = useState([]);
  const [allDoctors, setAllDoctors] = useState([]); // Store all doctors
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    specialty: 'all',
    location: 'all',
    language: 'all',
    availability: 'all'
  });
  const [sortBy, setSortBy] = useState('rating');
  const [searchQuery, setSearchQuery] = useState('');

  // Fetch doctors on component mount
  useEffect(() => {
    fetchDoctors();
  }, []);

  const fetchDoctors = async () => {
    try {
      console.log('Fetching doctors...');
      const response = await fetch('http://localhost:5000/api/doctors', {
        headers: {
          'Accept': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch doctors');
      }

      const result = await response.json();
      console.log('Received doctors data:', result);
      setAllDoctors(result.data || []); // Store all doctors
      setDoctors(result.data || []); // Set initial displayed doctors
      setLoading(false);
    } catch (error) {
      console.error('Error fetching doctors:', error);
      setError(error.message);
      setLoading(false);
    }
  };

  // Get unique values for filter options
  const getUniqueValues = (key) => {
    return ['all', ...new Set(allDoctors.map(doctor => doctor[key]))];
  };

  // Filter and sort doctors based on selected filters
  const getFilteredAndSortedDoctors = () => {
    return allDoctors
      .filter(doctor => {
        const matchesSearch = doctor.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
                              doctor.specialty.toLowerCase().includes(searchQuery.toLowerCase()) ||
                              doctor.hospital.toLowerCase().includes(searchQuery.toLowerCase());
        
        const matchesSpecialty = filters.specialty === 'all' || doctor.specialty === filters.specialty;
        const matchesLocation = filters.location === 'all' || doctor.location === filters.location;
        const matchesLanguage = filters.language === 'all' || doctor.languages.includes(filters.language);
        const matchesAvailability = filters.availability === 'all' || 
                                    (filters.availability === 'available' ? doctor.isAvailable : !doctor.isAvailable);

        return matchesSearch && matchesSpecialty && matchesLocation && matchesLanguage && matchesAvailability;
      })
      .sort((a, b) => {
        switch (sortBy) {
          case 'rating':
            return b.rating - a.rating;
          case 'experience':
            return b.experience - a.experience;
          case 'fee-low':
            return a.fee - b.fee;
          case 'fee-high':
            return b.fee - a.fee;
          default:
            return 0;
        }
      });
  };

  // Update displayed doctors when filters change
  useEffect(() => {
    setDoctors(getFilteredAndSortedDoctors());
  }, [filters, sortBy, searchQuery, allDoctors]);

  if (loading) {
    return (
      <div className="find-doctor-container loading">
        <div className="loading-spinner">
          <i className="fas fa-spinner fa-spin"></i>
          <p>Loading doctors...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="find-doctor-container error">
        <div className="error-message">
          <i className="fas fa-exclamation-circle"></i>
          <h2>Error Loading Doctors</h2>
          <p>{error}</p>
          <button onClick={fetchDoctors} className="retry-button">
            Try Again
          </button>
        </div>
      </div>
    );
  }

  const filteredDoctors = getFilteredAndSortedDoctors();

  return (
    <div className="find-doctor-container">
      <div className="search-section">
        <h1>Find a Doctor</h1>
        <div className="search-bar">
          <i className="fas fa-search"></i>
          <input
            type="text"
            placeholder="Search by doctor name, specialty, or hospital..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>
      </div>

      <div className="filters-section">
        <div className="filter-group">
          <label>Specialty:</label>
          <select
            value={filters.specialty}
            onChange={(e) => setFilters({ ...filters, specialty: e.target.value })}
          >
            {getUniqueValues('specialty').map(specialty => (
              <option key={specialty} value={specialty}>
                {specialty === 'all' ? 'All Specialties' : 
                  specialty.charAt(0).toUpperCase() + specialty.slice(1)}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label>Location:</label>
          <select
            value={filters.location}
            onChange={(e) => setFilters({ ...filters, location: e.target.value })}
          >
            {getUniqueValues('location').map(location => (
              <option key={location} value={location}>
                {location === 'all' ? 'All Locations' : location}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label>Language:</label>
          <select
            value={filters.language}
            onChange={(e) => setFilters({ ...filters, language: e.target.value })}
          >
            {['all', ...new Set(allDoctors.flatMap(doctor => doctor.languages))].map(language => (
              <option key={language} value={language}>
                {language === 'all' ? 'All Languages' : language}
              </option>
            ))}
          </select>
        </div>

        <div className="filter-group">
          <label>Availability:</label>
          <select
            value={filters.availability}
            onChange={(e) => setFilters({ ...filters, availability: e.target.value })}
          >
            <option value="all">All</option>
            <option value="available">Available</option>
            <option value="unavailable">Not Available</option>
          </select>
        </div>

        <div className="filter-group">
          <label>Sort By:</label>
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
          >
            <option value="rating">Rating</option>
            <option value="experience">Experience</option>
            <option value="fee-low">Fee: Low to High</option>
            <option value="fee-high">Fee: High to Low</option>
          </select>
        </div>
      </div>

      <div className="results-info">
        <p>Found {filteredDoctors.length} doctors</p>
      </div>

      <div className="doctors-grid">
        {filteredDoctors.map((doctor) => (
          <div key={doctor.id} className="doctor-card">
            <div className="doctor-image">
              <i className="fas fa-user-md"></i>
            </div>
            <div className="doctor-info">
              <h2>{doctor.name}</h2>
              <p className="specialty">
                {doctor.specialty.charAt(0).toUpperCase() + doctor.specialty.slice(1)}
              </p>
              
              <div className="doctor-qualifications">
                {doctor.qualifications.map((qual, index) => (
                  <span key={index} className="qualification-badge">
                    {qual}
                  </span>
                ))}
              </div>

              <div className="doctor-details">
                <p>
                  <i className="fas fa-hospital"></i>
                  {doctor.hospital}
                </p>
                <p>
                  <i className="fas fa-map-marker-alt"></i>
                  {doctor.location}
                </p>
                <p>
                  <i className="fas fa-graduation-cap"></i>
                  {doctor.experience} years experience
                </p>
                <p>
                  <i className="fas fa-language"></i>
                  {doctor.languages.join(', ')}
                </p>
                <p>
                  <i className="fas fa-rupee-sign"></i>
                  Consultation: ₹{doctor.fee}
                </p>
                <p>
                  <i className="fas fa-clock"></i>
                  {doctor.timing}
                </p>
                <p>
                  <i className="fas fa-users"></i>
                  {doctor.totalPatients.toLocaleString()} patients treated
                </p>
              </div>

              <div className="doctor-rating">
                <div className="stars">
                  {[...Array(5)].map((_, i) => (
                    <i
                      key={i}
                      className={`fas fa-star ${i < Math.floor(doctor.rating) ? 'filled' : ''}`}
                    ></i>
                  ))}
                </div>
                <span>{doctor.rating.toFixed(1)}</span>
              </div>
            </div>

            <div className="doctor-actions">
              <button 
                className={`book-appointment ${!doctor.isAvailable ? 'disabled' : ''}`}
                disabled={!doctor.isAvailable}
              >
                <i className="fas fa-calendar-check"></i>
                {doctor.isAvailable ? 'Book Appointment' : 'Not Available'}
              </button>
              <button className="view-profile">
                <i className="fas fa-user"></i>
                View Profile
              </button>
            </div>
          </div>
        ))}
      </div>

      {filteredDoctors.length === 0 && (
        <div className="no-results">
          <i className="fas fa-search"></i>
          <h2>No doctors found</h2>
          <p>Try adjusting your filters or search criteria</p>
        </div>
      )}
    </div>
  );
};

export default FindDoctorPage;
